(()=>{"use strict";(async function(){let t=await async function(t){try{let t=await fetch("https://jsonplaceholder.typicode.com/posts");return await t.json()}catch(t){return[]}}();t.splice(0,15);let e=document.querySelector(".posts");t.forEach((t=>{e.innerHTML+=`\n                <div class="post">\n                    <h3>${t.title}</h3>\n                    <div class="body">${t.body}</div>\n                    <button class="close">Удалить</button>\n                </div>\n            `}))})(),document.querySelector("body").onclick=function(t){"close"==t.target.className&&t.target.closest(".post").remove()}})();